mapper.update:
UPDATE applications
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM applications
WHERE job_id = #{jobId}
ORDER BY updated_at DESC, id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT * FROM applications
WHERE job_id = #{jobId} AND student_user_id = #{studentUserId}
LIMIT 1

SELECT * FROM applications
WHERE id = #{id} AND student_user_id = #{studentUserId}
LIMIT 1

SELECT a.id AS applicationId, s.full_name AS candidateName, MIN(YEAR(edu.start_date)) AS startYear, MAX(edu.degree_level) AS degreeLevel, a.status FROM applications a LEFT JOIN students s ON s.user_id = a.student_user_id LEFT JOIN education_experiences edu ON edu.student_user_id = a.student_user_id WHERE a.job_id = #{jobId}
AND s.full_name LIKE CONCAT('%', #{nameKeyword}, '%')
AND a.status = #{status}
GROUP BY a.id, s.full_name, a.status ORDER BY a.updated_at DESC, a.id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT a.id)
FROM applications a
LEFT JOIN students s ON s.user_id = a.student_user_id
WHERE a.job_id = #{jobId}

	AND s.full_name LIKE CONCAT('%', #{nameKeyword}, '%')


	AND a.status = #{status}
