mapper.select:
SELECT * FROM jobs
WHERE company_id = #{companyId}
ORDER BY updated_at DESC, id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT DISTINCT j.*
FROM jobs j
LEFT JOIN companies c ON j.company_id = c.company_id
LEFT JOIN job_tags jt ON j.id = jt.job_id
WHERE j.status = 20

	AND (j.title LIKE CONCAT('%', #{keyword}, '%') OR c.company_name LIKE CONCAT('%', #{keyword}, '%'))


	AND j.location LIKE CONCAT('%', #{location}, '%')


	AND j.type = #{type}


	AND jt.tag_id IN

		#{tagId}



	AND j.work_nature = #{workNature}

ORDER BY j.updated_at DESC, j.id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT j.id) FROM jobs j LEFT JOIN companies c ON j.company_id = c.company_id LEFT JOIN job_tags jt ON j.id = jt.job_id WHERE j.status = 20
AND (j.title LIKE CONCAT('%', #{keyword}, '%') OR c.company_name LIKE CONCAT('%', #{keyword}, '%'))
AND j.location LIKE CONCAT('%', #{location}, '%')
AND j.type = #{type}
AND jt.tag_id IN
#{tagId}
AND j.work_nature = #{workNature}

SELECT * FROM jobs
WHERE company_id = #{companyId}
AND id != #{excludeJobId}
AND status = 20
ORDER BY updated_at DESC
LIMIT #{limit}
