mapper.select:
SELECT * FROM jobs
WHERE company_id = #{companyId}
ORDER BY updated_at DESC, id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT j.id AS jobId, j.title, j.work_nature AS workNature, j.status, j.updated_at AS updatedAt, COALESCE(total_stats.received_num, 0) AS receivedNum, COALESCE(no_review_stats.no_review_num, 0) AS noReviewNum FROM jobs j LEFT JOIN ( SELECT job_id, COUNT(1) AS received_num FROM applications GROUP BY job_id ) total_stats ON total_stats.job_id = j.id LEFT JOIN ( SELECT job_id, COUNT(1) AS no_review_num FROM applications WHERE status = 10 GROUP BY job_id ) no_review_stats ON no_review_stats.job_id = j.id WHERE j.company_id = #{companyId}
AND j.title LIKE CONCAT('%', #{titleKeyword}, '%')
AND j.work_nature = #{workNature}
AND j.status = #{status}
ORDER BY j.updated_at DESC, j.id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1)
FROM jobs j
WHERE j.company_id = #{companyId}

	AND j.title LIKE CONCAT('%', #{titleKeyword}, '%')


	AND j.work_nature = #{workNature}


	AND j.status = #{status}

SELECT DISTINCT j.*
FROM jobs j
LEFT JOIN companies c ON j.company_id = c.company_id
LEFT JOIN job_tags jt ON j.id = jt.job_id
WHERE j.status = 20


		AND (j.title LIKE CONCAT('%', #{criteria.keyword}, '%')
			OR c.company_name LIKE CONCAT('%', #{criteria.keyword}, '%'))


		AND j.title LIKE CONCAT('%', #{criteria.title}, '%')


		AND c.company_name LIKE CONCAT('%', #{criteria.companyName}, '%')


		AND j.address_detail LIKE CONCAT('%', #{criteria.location}, '%')


		AND j.province_id = #{criteria.provinceId}


		AND j.city_id = #{criteria.cityId}



		AND (
			j.address_detail LIKE CONCAT('%', #{criteria.province}, '%')
			OR c.company_address LIKE CONCAT('%', #{criteria.province}, '%')
		)



		AND (
			j.address_detail LIKE CONCAT('%', #{criteria.city}, '%')
			OR c.company_address LIKE CONCAT('%', #{criteria.city}, '%')
		)



		AND (
		(j.min_salary IS NOT NULL AND j.max_salary IS NOT NULL AND j.max_salary >= #{criteria.minSalary} AND j.min_salary <= #{criteria.maxSalary})
		OR (j.min_salary IS NOT NULL AND j.max_salary IS NULL AND j.min_salary <= #{criteria.maxSalary})
		)


		AND j.max_salary IS NOT NULL AND j.max_salary >= #{criteria.minSalary}


		AND j.min_salary IS NOT NULL AND j.min_salary <= #{criteria.maxSalary}


		AND j.type = #{criteria.type}



	AND jt.tag_id IN

		#{tagId}






				AND c.nature_id IN (1, 2, 3, 4, 5, 6, 7, 8, 9, 13, 14)


				AND c.nature_id IN (10, 11, 12)









			AND j.work_nature = 1


			AND j.work_nature = 2


			AND j.work_nature = #{workNature}



ORDER BY j.updated_at DESC, j.id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT DISTINCT j.*, jf.saved_at FROM jobs j INNER JOIN job_favorites jf ON j.id = jf.job_id LEFT JOIN companies c ON j.company_id = c.company_id LEFT JOIN job_tags jt ON j.id = jt.job_id WHERE jf.student_user_id = #{studentUserId} AND j.status = 20
AND (j.title LIKE CONCAT('%', #{criteria.keyword}, '%') OR c.company_name LIKE CONCAT('%', #{criteria.keyword}, '%'))
AND j.title LIKE CONCAT('%', #{criteria.title}, '%')
AND c.company_name LIKE CONCAT('%', #{criteria.companyName}, '%')
AND j.address_detail LIKE CONCAT('%', #{criteria.location}, '%')
AND j.province_id = #{criteria.provinceId}
AND j.city_id = #{criteria.cityId}
AND ( j.address_detail LIKE CONCAT('%', #{criteria.province}, '%') OR c.company_address LIKE CONCAT('%', #{criteria.province}, '%') )
AND ( j.address_detail LIKE CONCAT('%', #{criteria.city}, '%') OR c.company_address LIKE CONCAT('%', #{criteria.city}, '%') )
AND (j.max_salary IS NULL OR j.max_salary >= #{criteria.minSalary})
AND (j.min_salary IS NULL OR j.min_salary <= #{criteria.maxSalary})
AND j.type = #{criteria.type}
AND jt.tag_id IN
#{tagId}
AND c.nature_id IN (1, 2, 3, 4, 5, 6, 7, 8, 9, 13, 14)
AND c.nature_id IN (10, 11, 12)
AND j.work_nature = 1
AND j.work_nature = 2
AND j.work_nature = #{workNature}
ORDER BY jf.saved_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM jobs
WHERE company_id = #{companyId}

	AND id != #{excludeJobId}

AND status = 20
ORDER BY updated_at DESC

	LIMIT #{limit}
